# Robot Rumble - Python Starter Bot
# Adaptive focus-fire strategy

def robot(state, unit):
    """Main entry point - called each turn for each robot."""

    # Get all units
    allies = [u for u in state.objs_by_team(state.our_team) if u.id != unit.id]
    enemies = list(state.objs_by_team(state.other_team))

    if not enemies:
        return Action.move(Direction.East)  # No enemies, advance

    # Helper: Manhattan distance
    def dist(a, b):
        return abs(a.coords.x - b.coords.x) + abs(a.coords.y - b.coords.y)

    # Find best target (lowest HP, then nearest)
    target = min(enemies, key=lambda e: (e.health, dist(unit, e)))

    # If adjacent, attack
    if dist(unit, target) == 1:
        direction = direction_to(unit.coords, target.coords)
        return Action.attack(direction)

    # Otherwise, move toward target
    return Action.move(direction_to(unit.coords, target.coords))

def direction_to(from_pos, to_pos):
    """Get direction from one position toward another."""
    dx = to_pos.x - from_pos.x
    dy = to_pos.y - from_pos.y

    # Prefer larger delta
    if abs(dx) >= abs(dy):
        return Direction.East if dx > 0 else Direction.West
    else:
        return Direction.North if dy > 0 else Direction.South
