// Robot Rumble - JavaScript Starter Bot
// Adaptive focus-fire strategy

function robot(state, unit) {
  // Get all units
  const allies = state.objsByTeam(state.ourTeam).filter(u => u.id !== unit.id);
  const enemies = state.objsByTeam(state.otherTeam);

  if (enemies.length === 0) {
    return Action.move(Direction.East); // No enemies, advance
  }

  // Helper: Manhattan distance
  const dist = (a, b) => 
    Math.abs(a.coords.x - b.coords.x) + Math.abs(a.coords.y - b.coords.y);

  // Find best target (lowest HP, then nearest)
  const target = enemies.reduce((best, e) => {
    if (!best) return e;
    if (e.health < best.health) return e;
    if (e.health === best.health && dist(unit, e) < dist(unit, best)) return e;
    return best;
  }, null);

  // If adjacent, attack
  if (dist(unit, target) === 1) {
    return Action.attack(directionTo(unit.coords, target.coords));
  }

  // Otherwise, move toward target
  return Action.move(directionTo(unit.coords, target.coords));
}

function directionTo(from, to) {
  const dx = to.x - from.x;
  const dy = to.y - from.y;

  // Prefer larger delta
  if (Math.abs(dx) >= Math.abs(dy)) {
    return dx > 0 ? Direction.East : Direction.West;
  } else {
    return dy > 0 ? Direction.North : Direction.South;
  }
}
